
function tensor_product_uv_column(u, v)
    """
    Purpose: computes the tensor product between two column vectors

    Inputs: 
    u size(m, 1) column vector, v size(n,1): 

    Outputs: 
    uv size(m*n,1) : the tensorporduct of the vectors u and v
    """
    m = size(u)[1]
    n = size(v)[1]
    uv = zeros(m*n)
    for i in 1:m
        uv[(i-1)*n + 1 : i * n] = u[i] * v
    end
    return uv
end

function tensor_product_uv_row(u, v)
    """
    Purpose: computes the tensor product between two row vectors

    Inputs: 
    u size(1, m) row vector, v size(1,n): 

    Outputs: 
    uv size(1,m*n) : the tensorporduct of the vectors u and v
    """
    m = size(u)[2]
    n = size(v)[2]
    uv = zeros(1, m*n)
    for i in 1:m
        uv[1, (i-1)*n + 1 : i * n] = u[1, i] * v
    end
    return uv
end

function tensor_product_AB(A,B)
    """
    Purpose: computes the tensor product between two matricies 

    Inputs: 
    A size(n,m), B size(r,s): the two matricies that you are taking the tensorproduct of 

    Outputs: 
    AB size(n*r,m*s) : the tensorporduct of the matrices A and B 

    Testing: tested in unit_tests_tensor_product_sbp.jl
    """
    size_A = size(A)
    size_B = size(B)
    #-- make sure both arguments are matrices
    @assert(length(size_A)==2)
    @assert(length(size_B)==2)

    AB = zeros(size_A[1]*size_B[1],size_A[2]*size_B[2])
    for i in 1:size_A[1]
        for j in 1:size_A[2]
            AB[(i-1)*size_B[1]+1:(i)*size_B[1],(j-1)*size_B[2]+1:(j)*size_B[2]] = A[i,j]*B
        end
    end

    return AB
end

function identity_matrix(n)
    """
    Purpose: computes an nxn identity matrix (really an array)

    Inputs: 
    n: the size of the identity matrix 

    Outputs: 
    I size(n,n): nxn identity matrix 

    Testing: no tests
    """
    I = zeros(n,n)
    for i in 1:n 
        I[i,i] = 1.0
    end
    return I
end

function construct_1d_cg(n,N,XI,QXI,PXI,MXI)
    """
    Purpose: construct a 1D continuous Galerkin like operator on N elements 
    using a given SBP operator. Note that the resulting operator is for 
    the domain [-1,1]. Also note that the assumption here is an SBP operator 
    with boundary nodes

    Inputs: 
    n             : number of nodes an element 
    N             : number of elements 
    XI size(n,1)  : computational domain on the element 
    QXI size(n,n) : Q operator on the element (transpose of the stifness matrix)
    PXI size(n,n) : P operator on the element (mass matrix)
    MXI size(n,n) : matrix that integrates int(f,x = xL,x) at the nodes

    Outputs: define num_nodes = n*N-(N-1)
    X size(num_nodes,1)         : the nodal distribution for the constructed operator 
    D size(num_nodes,num_nodes) : the differentiation matrix
    P size(num_odes,num_nodes)  : the norm (mass) matrix
    Q size(num_nodes,num_nodes) : transpose of the stifness matrix
    M size(num_nodes,num_nodes) : matrix that integrates int(f,x = xL,x) at the nodes
    tL size(num_nodes,1)        : interpolaton to the left boundary
    tR size(num_nodes,1)        : interpolation to the right boundary
    """
    num_nodes = n*N-(N-1)
    P = zeros(num_nodes,num_nodes)
    Q = zeros(num_nodes,num_nodes)
    M = zeros(num_nodes,num_nodes)
    D = zeros(num_nodes,num_nodes)
    tL = zeros(num_nodes,1)
    tR = zeros(num_nodes,1)
    X = zeros(num_nodes,1)

    tL[1,1] = -1
    tR[num_nodes,1] = 1

    
    #-- construct the nodal distribution (map between computational elements and -1 to 1)
    dx = 2/N
    for i = 1:N 
        xL = -1+(i-1)*dx 
        xR = xL+dx
        a = (xR-xL)/2.0
        b  = (xR+xL)/2

        X[(i-1)*(n-1)+1:i*(n-1)+1,1] = a*XI+b*ones(n,1)

        P[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1] = P[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1]+a*PXI[1:n,1:n]
        Q[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1] = Q[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1]+QXI[1:n,1:n]
        #M[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1] = M[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1]+a*MXI[1:n,1:n]

    end

    for i = 1:N 
        xL = -1+(i-1)*dx 
        xR = xL+dx
        a = (xR-xL)/2.0
        b  = (xR+xL)/2
        M[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1] = M[(i-1)*(n-1)+1:i*(n-1)+1,(i-1)*(n-1)+1:i*(n-1)+1]+a*MXI[1:n,1:n]
        for j = i*(n-1)+2:num_nodes 
            M[j,(i-1)*(n-1)+1:i*(n-1)+1] = M[j,(i-1)*(n-1)+1:i*(n-1)+1] + a*MXI[n,1:n]
        end
    end
    D = inv(P)*Q
    return X,D,P,Q,M,tL,tR
end
